package pattern;

public class p47 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
        System.out.println("===========================");
        for(int i=1;i<=5;i++)
        {
        	
        	for(int j=1;j<=5;j++)
        	{
        		if(i>=j)
        		{
        			System.out.print(i*j+" ");
            		
        		}
        		
        	}
        	
        	System.out.println();
        }
	}

}
